import api from '@/services/api'
export default {
  createUser() {
    return api().get('createUser')
  },
  login(code) {
    return api().post('login', {code: code})
  },
  tgValidate(codeToLogin, tgValidationCode){
    return api().post('tgValidate', {code: codeToLogin, tgCode: tgValidationCode})
  },
}
